class Disciplina extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Disciplina_model');
    } 

    /*
     * Listing of disciplina
     */
    function index()
    {
        $data['disciplina'] = $this->Disciplina_model->get_all_disciplina();
        
        $data['_view'] = 'disciplina/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new disciplina
     */
    function add()
    {   
        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'disciplina' => $this->input->post('disciplina'),
            );
            
            $disciplina_id = $this->Disciplina_model->add_disciplina($params);
            redirect('disciplina/index');
        }
        else
        {            
            $data['_view'] = 'disciplina/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a disciplina
     */
    function edit($id)
    {   
        // check if the disciplina exists before trying to edit it
        $data['disciplina'] = $this->Disciplina_model->get_disciplina($id);
        
        if(isset($data['disciplina']['id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'disciplina' => $this->input->post('disciplina'),
                );

                $this->Disciplina_model->update_disciplina($id,$params);            
                redirect('disciplina/index');
            }
            else
            {
                $data['_view'] = 'disciplina/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The disciplina you are trying to edit does not exist.');
    } 

    /*
     * Deleting disciplina
     */
    function remove($id)
    {
        $disciplina = $this->Disciplina_model->get_disciplina($id);

        // check if the disciplina exists before trying to delete it
        if(isset($disciplina['id']))
        {
            $this->Disciplina_model->delete_disciplina($id);
            redirect('disciplina/index');
        }
        else
            show_error('The disciplina you are trying to delete does not exist.');
    }
    
}
