    }  

    /*
     * Editing a itens_laboratorio
     */
    function edit($id)
    {   
        // check if the itens_laboratorio exists before trying to edit it
        $data['itens_laboratorio'] = $this->Itens_laboratorio_model->get_itens_laboratorio($id);
        
        if(isset($data['itens_laboratorio']['id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
